query:
fragment AuthorFragment on User {
  __typename
  id
  handle
  avatarImage {
    __typename
    uri
  }
  score
  isSuspended
  strikeData {
    __typename
    isFaded
    currentStrikeCount
  }
}

fragment ContentFragment on Content {
  __typename
  body
  segments {
    __typename
    ... on Text {
      __typename
      chunk
      noSpace
      lang {
        __typename
        code
      }
    }
    ... on Tag {
      __typename
      id
      name
      noSpace
    }
    ... on User {
      __typename
      id
      handle
    }
  }
}

mutation addComment($addCommentInput: AddCommentInput!) {
  __typename
  addComment(input: $addCommentInput) {
    __typename
    ... on SmartError {
      __typename
      message
    }
    ... on PostNotFoundError {
      __typename
      message
    }
    ... on AddCommentResult {
      __typename
      challenge {
        __typename
        id
        name
        authorInteractionsConnection {
          __typename
          interactionCount
        }
      }
      comment {
        __typename
        id
        ts {
          __typename
          createdAt
          updatedAt
        }
        author {
          __typename
          ...AuthorFragment
        }
        body {
          __typename
          ...ContentFragment
        }
        commentStats {
          __typename
          likeCount
          replyCount
          reportCount
        }
        commentContext {
          __typename
          liked
        }
        participationType
      }
      post {
        __typename
        id
        stats {
          __typename
          likeCount
          realCount
          applauseCount
          shareCount
          repostCount
          commentCount
          reportCount
        }
        willBeDeleted
      }
    }
    ... on TrollDetectorError {
      __typename
      message
      data
      indices
    }
  }
}
