query:
fragment ContentFragment on Content {
  __typename
  body
  segments {
    __typename
    ... on Text {
      __typename
      chunk
      noSpace
      lang {
        __typename
        code
      }
    }
    ... on Tag {
      __typename
      id
      name
      noSpace
    }
    ... on User {
      __typename
      id
      handle
    }
  }
}

fragment CommentFragment on Comment {
  __typename
  id
  ts {
    __typename
    createdAt
    updatedAt
  }
  body {
    __typename
    ...ContentFragment
  }
  commentStats {
    __typename
    likeCount
    replyCount
    reportCount
  }
  commentContext {
    __typename
    liked
  }
  participationType
}

mutation deleteComment($deleteCommentInput: DeleteCommentInput!) {
  __typename
  deleteComment(input: $deleteCommentInput) {
    __typename
    ... on DeleteCommentResult {
      __typename
      isSuccessful
      post {
        __typename
        id
        willBeDeleted
        stats {
          __typename
          likeCount
          realCount
          applauseCount
          shareCount
          repostCount
          commentCount
          reportCount
        }
        pinnedComment {
          __typename
          ...CommentFragment
        }
        ... on MultiMediaPost {
          __typename
          id
        }
      }
    }
    ... on SmartError {
      __typename
      message
    }
  }
}
