query:
mutation flagComment($flagCommentInput: FlagCommentInput!) {
  __typename
  flagComment(input: $flagCommentInput) {
    __typename
    ... on FlagCommentResult {
      __typename
      comment {
        __typename
        id
      }
      parentPost {
        __typename
        id
        stats {
          __typename
          likeCount
          realCount
          applauseCount
          shareCount
          repostCount
          commentCount
          reportCount
        }
      }
    }
    ... on SmartError {
      __typename
      message
    }
  }
}
