query:
fragment PageInfoFragment on PageInfo {
  __typename
  startCursor
  endCursor
  hasNextPage
  hasPreviousPage
  pageNumber
  count
  totalCount
}

fragment ContentFragment on Content {
  __typename
  body
  segments {
    __typename
    ... on Text {
      __typename
      chunk
      noSpace
      lang {
        __typename
        code
      }
    }
    ... on Tag {
      __typename
      id
      name
      noSpace
    }
    ... on User {
      __typename
      id
      handle
    }
  }
}

fragment UserFragment on User {
  __typename
  id
  score
  isAvailable
  isSuspended
  handle
  name
  avatarImage {
    __typename
    uri
  }
  strikeData {
    __typename
    isFaded
    currentStrikeCount
  }
}

fragment CommentFragment on Comment {
  __typename
  id
  ts {
    __typename
    createdAt
    updatedAt
  }
  author {
    __typename
    ...UserFragment
  }
  body {
    __typename
    body
    ...ContentFragment
  }
  commentStats {
    __typename
    likeCount
    replyCount
  }
  commentContext {
    __typename
    liked
  }
  participationType
}

fragment CommentPostingAccessControlContextFragment on CommentPostingAccessControlContext {
  __typename
  commentPostingAccess
  canComment
  cannotCommentErrorMessage
}

fragment CommentVisibilityAccessControlContextFragment on CommentVisibilityAccessControlContext {
  __typename
  cannotViewCommentErrorMessage
  canViewComment
  commentVisibilityAccess
}

query paginatedComments($postId: ID!, $first: Int, $after: String, $last: Int, $before: String, $includingAndAfter: String, $targetCommentId: ID) {
  __typename
  getPost(input: {id: $postId}) {
    __typename
    ... on SmartError {
      __typename
      message
    }
    ... on GetPostResult {
      __typename
      post {
        __typename
        ... on MultiMediaPost {
          __typename
          id
        }
        commentsConnection(postId: $postId, first: $first, after: $after, last: $last, before: $before, includingAndAfter: $includingAndAfter, targetCommentId: $targetCommentId) {
          __typename
          pageInfo {
            __typename
            ...PageInfoFragment
          }
          edges {
            __typename
            cursor
            node {
              __typename
              ...CommentFragment
            }
          }
          targetCommentError
        }
        commentPostingAccessControlContext {
          __typename
          ...CommentPostingAccessControlContextFragment
        }
        commentVisibilityAccessControlContext {
          __typename
          ...CommentVisibilityAccessControlContextFragment
        }
      }
    }
  }
}
