query:
fragment ContentFragment on Content {
  __typename
  body
  segments {
    __typename
    ... on Text {
      __typename
      chunk
      noSpace
      lang {
        __typename
        code
      }
    }
    ... on Tag {
      __typename
      id
      name
      noSpace
    }
    ... on User {
      __typename
      id
      handle
    }
  }
}

fragment UserFragment on User {
  __typename
  id
  handle
  name
  avatarImage {
    __typename
    uri
  }
  isSuspended
  score
}

fragment CommentFragment on Comment {
  __typename
  id
  ts {
    __typename
    createdAt
    updatedAt
  }
  author {
    __typename
    ...UserFragment
  }
  body {
    __typename
    ...ContentFragment
  }
  commentStats {
    __typename
    likeCount
    replyCount
    reportCount
  }
  participationType
}

mutation pinComment($pinCommentInput: PinCommentInput!) {
  __typename
  pinComment(input: $pinCommentInput) {
    __typename
    ... on PinCommentResult {
      challenge {
        __typename
        id
        name
        authorInteractionsConnection {
          __typename
          interactionCount
        }
      }
      post {
        __typename
        id
        pinnedComment {
          __typename
          ...CommentFragment
        }
      }
      challenge {
        __typename
        id
        name
        pinnedComment {
          __typename
          ...CommentFragment
        }
      }
    }
    ... on SmartError {
      __typename
      message
    }
  }
}
