query:
mutation ReactOnComment($reactOnCommentInput: ReactOnCommentInput!) {
  __typename
  reactOnComment(input: $reactOnCommentInput) {
    __typename
    ... on ReactOnCommentResult {
      __typename
      challenge {
        __typename
        id
        name
        authorInteractionsConnection {
          __typename
          interactionCount
        }
      }
      comment {
        __typename
        id
        commentContext {
          __typename
          liked
        }
        commentStats {
          __typename
          likeCount
        }
      }
    }
    ... on SmartError {
      __typename
      message
    }
  }
}
