query getFollowersList($input: GetFollowersListInput!, $first: Int, $after: String, $last: Int, $before: String) {
  __typename
  getFollowersList(input: $input) {
    __typename
    ... on SmartError {
      message
    }
    ... on GetFollowersListResult {
      user {
        __typename
        id
        followersList(first: $first, after: $after, last: $last, before: $before) {
          __typename
          pageInfo {
            __typename
            startCursor
            endCursor
            hasNextPage
            hasPreviousPage
          }
          edges {
            __typename
            cursor
            node {
              __typename
              key: id
              id
              name
              handle
              avatarImage {
                __typename
                uri
              }
              realIdVerificationStatus
              realIdFace {
                __typename
                uri
              }
              score
              currentUserContext {
                __typename
                followingUser
              }
            }
          }
        }
      }
    }
  }
}
