query:
mutation removeFollower($removeFollowerInput: RemoveFollowerInput!) {
  __typename
  removeFollower(input: $removeFollowerInput) {
    __typename
    ... on RemoveFollowerResult {
      currentUser {
        __typename
        id
        stats {
          __typename
          followingCount
          followerCount
          postCount
        }
      }
    }
    ... on SmartError {
      message
    }
  }
}
