query:
mutation reactOnPost($reactOnPostInput: ReactOnPostInput!) {
  __typename
  reactOnPost(input: $reactOnPostInput) {
    __typename
    ... on ReactOnPostResult {
      __typename
      challenge {
        __typename
        id
        name
        authorInteractionsConnection {
          __typename
          interactionCount
        }
      }
      post {
        __typename
        ... on MultiMediaPost {
          __typename
          id
          stats {
            __typename
            likeCount
            realCount
            applauseCount
            shareCount
            repostCount
            commentCount
            reportCount
          }
          postContext {
            __typename
            liked
            realed
            applauded
          }
          canComment
          willBeDeleted
        }
      }
    }
    ... on SmartError {
      __typename
      message
    }
  }
}
