query:
mutation ReactOnReply($reactOnReplyInput: ReactOnReplyInput!) {
  __typename
  reactOnReply(input: $reactOnReplyInput) {
    __typename
    ... on ReactOnReplyResult {
      __typename
      challenge {
        __typename
        id
        name
        authorInteractionsConnection {
          __typename
          interactionCount
        }
      }
      reply {
        __typename
        id
        replyContext {
          __typename
          liked
        }
        replyStats {
          __typename
          likeCount
        }
      }
    }
    ... on SmartError {
      __typename
      message
    }
  }
}
